// Build docker command based on action
const action = $input.first().json.action?.toLowerCase() || 'ps';
const containerName = $input.first().json.containerName || '';

let command = '';
let requiresContainer = true;

switch(action) {
  case 'ps':
  case 'list':
    command = 'docker ps -a --format "table {{.Names}}\t{{.Status}}\t{{.Ports}}\t{{.Image}}"';
    requiresContainer = false;
    break;
  case 'running':
    command = 'docker ps --format "table {{.Names}}\t{{.Status}}\t{{.Ports}}"';
    requiresContainer = false;
    break;
  case 'stats':
    command = containerName 
      ? `docker stats ${containerName} --no-stream --format "table {{.Name}}\t{{.CPUPerc}}\t{{.MemUsage}}\t{{.NetIO}}"`
      : 'docker stats --no-stream --format "table {{.Name}}\t{{.CPUPerc}}\t{{.MemUsage}}\t{{.NetIO}}"';
    requiresContainer = false;
    break;
  case 'logs':
    command = `docker logs ${containerName} --tail 100`;
    break;
  case 'start':
    command = `docker start ${containerName} && docker ps --filter name=${containerName} --format "table {{.Names}}\t{{.Status}}"`;
    break;
  case 'stop':
    command = `docker stop ${containerName} && docker ps -a --filter name=${containerName} --format "table {{.Names}}\t{{.Status}}"`;
    break;
  case 'restart':
    command = `docker restart ${containerName} && docker ps --filter name=${containerName} --format "table {{.Names}}\t{{.Status}}"`;
    break;
  case 'inspect':
    command = `docker inspect ${containerName} | head -100`;
    break;
  case 'images':
    command = 'docker images --format "table {{.Repository}}\t{{.Tag}}\t{{.Size}}\t{{.CreatedSince}}"';
    requiresContainer = false;
    break;
  case 'volumes':
    command = 'docker volume ls';
    requiresContainer = false;
    break;
  case 'networks':
    command = 'docker network ls';
    requiresContainer = false;
    break;
  case 'compose-ps':
    command = 'docker compose ps 2>/dev/null || docker-compose ps';
    requiresContainer = false;
    break;
  default:
    return [{ json: { error: `Unknown action: ${action}. Valid actions: ps, list, running, stats, logs, start, stop, restart, inspect, images, volumes, networks, compose-ps` } }];
}

if (requiresContainer && !containerName) {
  return [{ json: { error: `Container name required for action: ${action}` } }];
}

return [{ json: { command, action, containerName } }];

// Format the docker output
const result = $input.first().json;
const inputData = $('Prepare Command').first().json;

return [{
  json: {
    action: inputData.action,
    containerName: inputData.containerName || 'N/A',
    status: 'success',
    output: result.stdout || '',
    errors: result.stderr || null,
    timestamp: new Date().toISOString()
  }
}];
