// Build Jellyfin API request based on action
const action = $input.first().json.action?.toLowerCase() || 'status';
const paramsStr = $input.first().json.params || '{}';
let params = {};

try {
  params = typeof paramsStr === 'string' ? JSON.parse(paramsStr) : paramsStr;
} catch(e) {
  params = {};
}

const baseUrl = 'http://localhost:20001';
let endpoint = '';
let method = 'GET';
let body = null;

switch(action) {
  case 'status':
  case 'info':
    endpoint = '/System/Info';
    break;
  case 'health':
    endpoint = '/health';
    break;
  case 'users':
    endpoint = '/Users';
    break;
  case 'sessions':
    endpoint = '/Sessions';
    break;
  case 'libraries':
  case 'items':
    endpoint = '/Library/VirtualFolders';
    break;
  case 'scan':
  case 'refresh':
    endpoint = '/Library/Refresh';
    method = 'POST';
    break;
  case 'activity':
    endpoint = '/System/ActivityLog/Entries';
    break;
  case 'scheduled-tasks':
    endpoint = '/ScheduledTasks';
    break;
  case 'search':
    const searchTerm = params.query || params.searchTerm || '';
    endpoint = `/Items?searchTerm=${encodeURIComponent(searchTerm)}&Recursive=true&Limit=20`;
    break;
  case 'playing':
    endpoint = '/Sessions?ActiveWithinSeconds=960';
    break;
  case 'logs':
    endpoint = '/System/Logs';
    break;
  default:
    return [{ json: { error: `Unknown action: ${action}. Valid actions: status, info, health, users, sessions, libraries, items, scan, refresh, activity, scheduled-tasks, search, playing, logs` } }];
}

return [{ json: { url: baseUrl + endpoint, method, body, action } }];

// Format the Jellyfin API response
const result = $input.first().json;
const inputData = $('Prepare Request').first().json;

// Truncate large responses
let output = result;
if (typeof result === 'object' && Array.isArray(result) && result.length > 20) {
  output = {
    totalItems: result.length,
    items: result.slice(0, 20),
    note: `Showing first 20 of ${result.length} items`
  };
}

return [{
  json: {
    action: inputData.action,
    status: 'success',
    data: output,
    timestamp: new Date().toISOString()
  }
}];
