// Build systemctl command based on action and service name
const action = $input.first().json.action?.toLowerCase() || 'status';
const serviceName = $input.first().json.serviceName || '';

let command = '';
let requiresService = true;

switch(action) {
  case 'status':
    command = serviceName 
      ? `systemctl status ${serviceName} --no-pager` 
      : 'systemctl list-units --type=service --state=running --no-pager | head -30';
    requiresService = false;
    break;
  case 'start':
    command = `systemctl start ${serviceName} && systemctl status ${serviceName} --no-pager`;
    break;
  case 'stop':
    command = `systemctl stop ${serviceName} && systemctl status ${serviceName} --no-pager`;
    break;
  case 'restart':
    command = `systemctl restart ${serviceName} && systemctl status ${serviceName} --no-pager`;
    break;
  case 'enable':
    command = `systemctl enable ${serviceName} && systemctl is-enabled ${serviceName}`;
    break;
  case 'disable':
    command = `systemctl disable ${serviceName} && systemctl is-enabled ${serviceName}`;
    break;
  case 'list':
    command = 'systemctl list-units --type=service --no-pager | head -50';
    requiresService = false;
    break;
  case 'failed':
    command = 'systemctl list-units --type=service --state=failed --no-pager';
    requiresService = false;
    break;
  case 'logs':
    command = `journalctl -u ${serviceName} --no-pager -n 50`;
    break;
  default:
    return [{ json: { error: `Unknown action: ${action}. Valid actions: status, start, stop, restart, enable, disable, list, failed, logs` } }];
}

if (requiresService && !serviceName) {
  return [{ json: { error: `Service name required for action: ${action}` } }];
}

return [{ json: { command, action, serviceName } }];

// Format the service control output
const result = $input.first().json;
const inputData = $('Prepare Command').first().json;

return [{
  json: {
    action: inputData.action,
    serviceName: inputData.serviceName || 'N/A',
    status: 'success',
    output: result.stdout || '',
    errors: result.stderr || null,
    timestamp: new Date().toISOString()
  }
}];
