// Determine which system info command to run based on infoType
const infoType = $input.first().json.infoType?.toLowerCase() || 'all';

let command = '';

switch(infoType) {
  case 'cpu':
    command = "top -bn1 | head -5 && echo '---CPU INFO---' && lscpu | grep -E 'Model name|CPU\(s\)|Thread|Core'";
    break;
  case 'memory':
  case 'ram':
    command = "free -h && echo '---MEMORY DETAILS---' && cat /proc/meminfo | head -10";
    break;
  case 'disk':
    command = "df -h && echo '---LARGEST DIRECTORIES---' && du -sh /home/* 2>/dev/null | sort -hr | head -10";
    break;
  case 'network':
    command = "ip addr show | grep -E 'inet |link/' && echo '---LISTENING PORTS---' && ss -tlnp | head -20";
    break;
  case 'processes':
    command = "ps aux --sort=-%mem | head -15";
    break;
  case 'uptime':
    command = "uptime && echo '---SYSTEM INFO---' && uname -a";
    break;
  case 'all':
  default:
    command = "echo '=== UPTIME ===' && uptime && echo '\n=== CPU ===' && top -bn1 | head -5 && echo '\n=== MEMORY ===' && free -h && echo '\n=== DISK ===' && df -h | grep -E '^/dev/' && echo '\n=== NETWORK ===' && ip addr show | grep 'inet ' | head -5";
    break;
}

return [{ json: { command } }];

// Format the output for better readability
const result = $input.first().json;
const stdout = result.stdout || '';
const stderr = result.stderr || '';
const infoType = $('When Executed by Another Workflow').first().json.infoType || 'all';

return [{
  json: {
    infoType,
    status: 'success',
    output: stdout,
    errors: stderr || null,
    timestamp: new Date().toISOString()
  }
}];
